package day_1;

public class p3 {
	static int p=10,q=30;
	int a=10,b=20;
	void inst() {
		System.out.println(a+b);
		System.out.println(p+q);
		
	}

	
	static void show() {
		int x=10,y=20;
		System.out.println(p+q);
		System.out.println(x+y);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		p3 a1=new p3();
		a1.inst();
		
		a1.show();
		

	}

}
